def decline_users(n):
    last_digit = n % 10
    second_last_digit = (n // 10) % 10
    
    if second_last_digit == 1:
        return f"{n} –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π"
    
    if last_digit == 1:
        return f"{n} –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å"
    elif last_digit in [2, 3, 4]:
        return f"{n} –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è"
    else:
        return f"{n} –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª–µ–π"
    


def register_handlers(dp: Dispatcher):
    dp.register_message_handler(cmd_start, commands= ['start'], state = "*")
    dp.register_callback_query_handler(start_actions, text_startswith = "start_")
    dp.register_callback_query_handler(about_actions, text_startswith = "about_")
    dp.register_callback_query_handler(payoff_application, text = "payoff")
    dp.register_callback_query_handler(process_slide, text_startswith = "slide_")
    dp.register_callback_query_handler(process_add, text_startswith = "addtobucket_")
    dp.register_callback_query_handler(start_pay, text_startswith = "pay_")
    dp.register_callback_query_handler(proc_clear_bucket, text = "clear_bucket")
    dp.register_callback_query_handler(payed, text_startswith = "payed_")
    dp.register_message_handler(ai_response)



async def ai_response(message: types.Message, state: FSMContext):
    rag_system = message.bot['rag_system']
    waitmsg = await message.reply("‚è≥ –ì–µ–Ω–µ—Ä–∏—Ä—É—é –æ—Ç–≤–µ—Ç, –æ–∂–∏–¥–∞–π—Ç–µ...")
    response = await rag_system.generate_response(message.text)
    await waitmsg.delete()
    await message.reply(response[0])


async def cmd_start(message: types.Message, state: FSMContext):
    if not message.from_user.username:
        return await message.answer("‚õî –î–ª—è –∫–æ—Ä—Ä–µ–∫—Ç–Ω–æ–≥–æ –∏—Å–ø–æ–ª—å–∑–æ–≤–∞–Ω–∏—è –Ω–∞—à–µ–≥–æ –±–æ—Ç–∞ –ø—Ä–æ—Å–∏–º –í–∞—Å —É—Å—Ç–∞–Ω–æ–≤–∏—Ç—å —é–∑–µ—Ä–Ω–µ–π–º (–∏–º—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è) –≤ Telegram.")
    
    referal_id = int(decode_payload(message.get_args())) if message.get_args() else MAIN_REFERAL_ID
    if message.get_args():
        await message.bot.send_message(chat_id= referal_id, text = f"üéâ –ù–æ–≤—ã–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å @{message.from_user.username} –∑–∞—à–µ–ª –ø–æ –í–∞—à–µ–π —Ä–µ—Ñ–µ—Ä–∞–ª—å–Ω–æ–π —Å—Å—ã–ª–∫–µ!")
        
    await state.finish()

    if await add_user(message.from_user.username, message.from_user.id, referal_id):
        await message.answer("üëã –ó–¥—Ä–∞–≤—Å—Ç–≤—É–π—Ç–µ!\n–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ <b>BIO ACTIVE</b>.\n\n‚ú® –û—Ç–∫—Ä–æ–π—Ç–µ –¥–ª—è —Å–µ–±—è —Å–æ–≤—Ä–µ–º–µ–Ω–Ω—ã–µ –ø–∏—â–µ–≤—ã–µ –¥–æ–±–∞–≤–∫–∏ –Ω–∞ –æ—Å–Ω–æ–≤–µ –ø–µ–ø—Ç–∏–¥–æ–≤ –¥–ª—è –≤–æ—Å—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω–∏—è –∏ –æ–±–Ω–æ–≤–ª–µ–Ω–∏—è –æ—Ä–≥–∞–Ω–∏–∑–º–∞ –±–µ–∑ –ª–∏—à–Ω–∏—Ö —Ä–∏—Å–∫–æ–≤ –∏ –ø–æ–±–æ—á–Ω—ã—Ö —ç—Ñ—Ñ–µ–∫—Ç–æ–≤.\n\nüå∑ –ü—Ä–∏—Å–æ–µ–¥–∏–Ω—è–π—Ç–µ—Å—å –∫ –Ω–∞—à–µ–º—É Telegram-–∫–∞–Ω–∞–ª—É, —á—Ç–æ–±—ã —É–∑–Ω–∞—Ç—å –±–æ–ª—å—à–µ –æ –ø–µ–ø—Ç–∏–¥–∞—Ö –∏ –ø–æ–æ–±—â–∞—Ç—å—Å—è —Å –µ–¥–∏–Ω–æ–º—ã—à–ª–µ–Ω–Ω–∏—Ü–∞–º–∏!", reply_markup= start_kb())
    else:
        await message.answer("üëã –ó–¥—Ä–∞–≤—Å—Ç–≤—É–π—Ç–µ! –î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å –≤ <b>BIO ACTIVE</b>\n\n–í—ã–±–µ—Ä–∏—Ç–µ –∏–Ω—Ç–µ—Ä–µ—Å—É—é—â–∏–π –≤–∞—Å —Ä–∞–∑–¥–µ–ª –≤ –º–µ–Ω—é üëá", reply_markup= main_kb())
    

